mapper.select:
SELECT T.* FROM (
SELECT order_id, u_id, order_status, total_price, pickup_method as orderType, order_time, total_quantity,
ROW_NUMBER() OVER ( PARTITION BY DATE(order_time) ORDER BY order_time ASC ) as daily_order_num FROM orders ) AS T WHERE T.order_status IS NULL ORDER BY T.order_time DESC;

mapper.update:
UPDATE orders
SET
    order_status = #{status}
WHERE
    order_id = #{orderId}
